    public void ReadBook(ArrayList<Book> book){
        DefaultTableModel model = (DefaultTableModel)BookTable.getModel();
        for(Book b:book){
            model.addRow(new Object[] {b.getTitle(),b.getAuthor(),b.getLanguage(),b.getPages(),b.getType()});
        }
    }

